+  publish-preview:
+    name: Publish Preview Dashboard
+    runs-on: ubuntu-latest
+    needs: test
+    if: always() && github.ref != 'refs/heads/main'
+
+    steps:
+      # Checkout repository
+      - name: Checkout
+        uses: actions/checkout@v4.1.1
+
+      # Download all artifacts for preview
+      - name: Download artifacts
+        uses: actions/download-artifact@v4
+        with:
+          path: ./reports
+
+      # Extract all HTML reports for preview dashboard
+      - name: Extract HTML reports
+        shell: bash
+        run: |
+          cd reports
+          for dir in report-*; do
+            if [ -d "$dir" ] && [ -f "$dir/playwright-report.zip" ]; then
+              unzip -q "$dir/playwright-report.zip" -d "$dir"
+              rm "$dir/playwright-report.zip"
+            fi
+          done
+
+      # Generate preview dashboard
+      - name: Generate dashboard (preview)
+        run: |
+          BRANCH_NAME_SAFE="${GITHUB_REF_NAME//\//-}"
+
+          metadata='{
+            "publishTimestamp": "'"$(date -u '+%Y-%m-%d %H:%M UTC')"'",
+            "commitSha": "'"$GITHUB_SHA"'",
+            "runNumber": "'"$GITHUB_RUN_NUMBER"'",
+            "runUrl": "https://github.com/'"$GITHUB_REPOSITORY"'/actions/runs/'"$GITHUB_RUN_ID"'"
+          }'
+
+          METADATA_JSON="$metadata" node scripts/generate-index.js
+
+          # Remove old preview folder
+          rm -rf previews/$BRANCH_NAME_SAFE
+
+          # Create fresh preview folder
+          mkdir -p "previews/$BRANCH_NAME_SAFE"
+
+          # Copy only the fresh dashboard and reports
+          cp reports/index.html "previews/$BRANCH_NAME_SAFE/"
+          cp -r reports/report-* "previews/$BRANCH_NAME_SAFE/" || true
+          cp -r reports/jsonReports "previews/$BRANCH_NAME_SAFE/" || true
+          cp -r reports/junit "previews/$BRANCH_NAME_SAFE/" || true
+
+          # Add cache-control file for CDN invalidation
+          echo "Cache-Control: no-cache, no-store, must-revalidate" > "previews/$BRANCH_NAME_SAFE/.cache-control"
+
+          # Touch index.html to force Cloudflare to refresh cached version
+          touch "previews/$BRANCH_NAME_SAFE/index.html"
+
+      # Publish preview dashboard to gh-pages
+      - name: Publish preview to Pages
+        uses: peaceiris/actions-gh-pages@v3.9.2
+        with:
+          github_token: ${{ secrets.PAT }}
+          publish_dir: ./previews
+          commit_message: "Publish preview dashboard for ${{ github.ref_name }} [skip ci]"
+          keep_files: true
+
+      # Comment preview link on PR
+      - name: Comment preview link on PR
+        if: github.event_name == 'pull_request'
+        uses: actions/github-script@v7
+        with:
+          script: |
+            const branch = process.env.GITHUB_REF_NAME.replace(/\//g, "-");
+            const url  = `https://smenouillard.github.io/playwright-portfolio/${branch}/`;
+
+            const body = [
+              "### Preview Dashboard Ready",
+              "",
+              "**URL:**  ",
+              url,
+              "",
+              "_This link updates automatically with each push to this branch._"
+            ].join("\n");
+
+            github.rest.issues.createComment({
+              issue_number: context.issue.number,
+              owner: context.repo.owner,
+              repo: context.repo.repo,
+              body
+            });
