+    await this.page.goto('input-form-demo');
+    await this.page.waitForLoadState('domcontentloaded');
+  }
+
+  // fill all form fields
+  async fillForm(data: {
+    name: string;
+    email: string;
+    password: string;
+    company: string;
+    website: string;
+    country?: string;
+    city: string;
+    address1: string;
+    address2: string;
+    state: string;
+    zip: string;
