+    await this.cityInput.fill(data.city);
+    await this.address1Input.fill(data.address1);
+    await this.address2Input.fill(data.address2);
+    await this.stateInput.fill(data.state);
+    await this.zipInput.fill(data.zip);
+  }
+}
