+
+test.describe('Logout tests', () => {
+
+  test('Logout and verify redirect', async ({ page, loginPage }) => {
+    // open login page
+    await loginPage.goto();
+
+    // login
+    await loginPage.login(secrets.internet.username, secrets.internet.password);
+
